import React, { useState } from "react";

function App() {
  const [contact, setContact] = useState({
    fName: "",
    lName: "",
    email: ""
  });

  function handleChange(event) {

    const [name, value] = event.target;
    setContact((prevValue) => {
      if (name === "fname") {
        return {
          fName: value,
          lName: prevValue.lName,
          email: prevValue.email
        };
      }
      else if(name==="lname"){
        return {
          fName: prevValue.fname,
          lName: prevValue.lName,
          email: prevValue.email
        };
      }
    });
  }

  return (
    <div className="container">
      <h1>
        Hello {contact.fName} {contact.lName}
      </h1>
      <p>{contact.email}</p>
      <form>
        <input onClick={handleChange} name="fName" placeholder="First Name" value={contact.fName} />
        <input onClick={handleChange} name="lName" placeholder="Last Name" value={contact.lName} />
        <input onClick={handleChange} name="email" placeholder="Email" value={contact.email} />
        <button>Submit</button>
      </form>
    </div>
  );
}

export default App;
