import React, { useState } from "react";

function App() {
  const [colour,setColour]=useState("white");

  function setCol()
  {
    if(colour=="white")
      setColour("black");
  }
  function setWhite(){
    console.log("White");
  }
  function setBlack(){
    console.log("Black");
  }
  return (
    <div className="container">
      <h1>Hello</h1>
      <input type="text" placeholder="What's your name?" />
      <button onMouseOut={setWhite} onMouseOver={setBlack} >Submit</button>
    </div>
  );
}

export default App;
