import React, {useState} from "react";

let time = new Date().toLocaleTimeString(); 

function App() {
  let time = new Date().toLocaleTimeString(); 
  const [Time,setTime]= useState(time);

  function displayTime(){
    let time = new Date().toLocaleTimeString(); 
    setTime(time);
  }

  return (
    <div className="container">
      <h1>{Time}</h1>
      <button onClick={displayTime}>Get Time</button>
    </div>
  );
}

export default App;
